class Ponteiro(pygame.sprite.Sprite):
    def __init__(self):
        pygame.sprite.Sprite.__init__(self)
        self.image = pygame.Surface((10,50))
        self.image.fill(RED)
        self.rect = self.image.get_rect()
        self.speedx = 0
        self.rect.x = 125
        self.rect.y = 400
        self.speedx = velocidade
    
    def update(self):
        self.rect.x += self.speedx
        if self.rect.x > 915:
            self.speedx = velocidade*-1
        if self.rect.x < 125:
            self.speedx = velocidade
        
    def treinar(self):
        global velocidade
        velocidade += 2

class Acerto(pygame.sprite.Sprite):
    def __init__(self):
        pygame.sprite.Sprite.__init__(self)
        self.image = pygame.Surface((comprimento,50))
        self.image.fill(GREEN)
        self.rect = self.image.get_rect()
        self.rect.x = 475
        self.rect.y = 400

    def emagrece(self):
        global comprimento
        if comprimento <= 300:
            self.rect.x -= 5
            comprimento += 10
            self.image = pygame.Surface((comprimento,50))
            self.image.fill(GREEN)
        else:
            print("limite")
            
    def engorda(self):
        global comprimento
        if comprimento >= 50: 
            self.rect.x += 5
            comprimento -= 10
            self.image = pygame.Surface((comprimento,50))
            self.image.fill(GREEN)
        else:
            print("limite")

